import React, { useEffect, useState } from "react";
import { getAirQualityByStation } from "../services/api";

const AirQualityDisplay = ({ stationId }) => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function fetchData() {
      if (!stationId) return;
      setLoading(true);
      setError(null);
      try {
        const response = await getAirQualityByStation(stationId);
        // Якщо API повертає один об'єкт — перетворимо в масив для зручності
        setData(Array.isArray(response) ? response : [response]);
      } catch (err) {
        setError("Помилка при завантаженні даних якості повітря");
      } finally {
        setLoading(false);
      }
    }
    fetchData();
  }, [stationId]);

  if (!stationId) return <p>Оберіть станцію, щоб переглянути дані про якість повітря.</p>;
  if (loading) return <p>Завантаження даних...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;
  if (!data.length) return <p>Немає даних для вибраної станції.</p>;

  const record = data[data.length - 1]; // беремо останній запис

  return (
    <div style={{ marginTop: "20px" }}>
      <h3>Якість повітря на станції "{record.station.name}"</h3>
      <table
        style={{
          borderCollapse: "collapse",
          width: "100%",
          marginTop: "10px",
          border: "1px solid #ccc",
        }}
      >
        <thead>
          <tr style={{ backgroundColor: "#f4f4f4" }}>
            <th style={{ border: "1px solid #ccc", padding: "8px" }}>Параметр</th>
            <th style={{ border: "1px solid #ccc", padding: "8px" }}>Значення</th>
          </tr>
        </thead>
        <tbody>
          <tr><td>PM2.5</td><td>{record.pm25}</td></tr>
          <tr><td>PM10</td><td>{record.pm10}</td></tr>
          <tr><td>CO₂</td><td>{record.co2}</td></tr>
          <tr><td>O₃</td><td>{record.o3}</td></tr>
          <tr><td>NO₂</td><td>{record.no2}</td></tr>
          <tr><td>Температура</td><td>{record.temperature} °C</td></tr>
          <tr><td>Вологість</td><td>{record.humidity} %</td></tr>
        </tbody>
      </table>
    </div>
  );
};

export default AirQualityDisplay;
