import React, { useState } from 'react';
import StationSelector from './components/StationSelector';
import AirQualityDisplay from './components/AirQualityDisplay';

function App() {
  // Зберігаємо ID вибраної станції
  const [selectedStation, setSelectedStation] = useState(null);

  return (
    <div style={{ maxWidth: '600px', margin: '40px auto', fontFamily: 'Arial, sans-serif' }}>
      <h1 style={{ textAlign: 'center' }}>Система моніторингу якості повітря</h1>

      {/* Компонент вибору станції */}
      <StationSelector onSelect={setSelectedStation} />

      {/* Компонент відображення даних */}
      <AirQualityDisplay stationId={selectedStation} />
    </div>
  );
}

export default App;
